class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        pairs = n // 2
        dif, res = defaultdict(list), []
        for i in range(pairs):
            a = nums[i]
            b = nums[n - i - 1]
            diff = abs(a - b)
            x = max(a, b, k - a, k - b)
            dif[diff].append(x)
            res.append(x)

        res.sort()
        ans = n
        for diff, xl in dif.items():
            t = bisect_left(res, diff) * 2 + (pairs - bisect_left(res, diff))
            for x in xl:
                if x < diff:
                    t -= 2
                else:
                    t -= 1
            ans = min(ans, t)
        return ans
