class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        # for i in range(len(nums) // 2):
        #     print(str(nums[i]) + " " + str(nums[len(nums) - i - 1]))
        diff = {}
        for i in range(len(nums) // 2):
            curDiff = abs(nums[i] - nums[len(nums) - i - 1])
            if curDiff in diff:
                diff[curDiff] += 1
            else:
                diff[curDiff] = 1
        freq = []
        for i in diff:
            freq.append((i, diff[i]))
        freq.sort(key = lambda x: (-x[1], x[0]))
        # print(freq)
        res = 99999999
        for i in range(0, min(5, len(freq))):
            cnt = 0
            x = freq[i][0]
            # print("x is: " + str(x))
            for i in range(len(nums) // 2):
                a = nums[i]
                b = nums[len(nums) - i - 1]
                a, b = max(a,b), min(a,b)
                if a - b < x:
                    if a - x >= 0:
                        cnt += 1
                    elif b + x > k:
                        cnt += 2
                    else:
                        cnt += 1
                elif a - b > x:
                    cnt += 1
            # print(cnt)
            res = min(res, cnt)
        return res
