class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        c=Counter()
        n=len(nums)
        ch=inf
        for x,y in zip(nums[:n//2],nums[n//2:][::-1]):
            c[abs(x-y)]+=1
            ch=min(ch,max(x,y))
            ch=min(ch,k-min(x,y))
        mx=max(c.values())
        target=min([x for x in c if c[x]==mx])
        ans=0
        for x,y in zip(nums[:n//2],nums[n//2:][::-1]):
            if abs(x-y)!=target:
                ans+=1
                if max(x,y)<target and k-min(x,y)<target:
                    ans+=1
        for x in c:
            if x<=ch:
                ans=min(ans,n//2-c[x])
        return ans
