class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> diff;
        int n = nums.size(), min_diff = INT_MAX, max_freq = 0, max_freq_diff = 0, max_freq2 = 0, max_freq_diff2 = 0;
        for(int i=0; i<n/2; i++)
            diff[abs(nums[i] - nums[n-i-1])]++;

        for(auto it: diff)
        {
            min_diff = min(min_diff, it.first);
            if(it.second > max_freq)
            {
                max_freq = max(max_freq, it.second);
                max_freq_diff = it.first;
            } 
            else if(it.second == max_freq)
            {
                if(max_freq_diff > it.first)
                    max_freq_diff = it.first;
            }
        }

        for(auto it: diff)
        {
            if(it.second == max_freq)
                continue;
            if(it.second > max_freq2)
            {
                max_freq2 = max(max_freq2, it.second);
                max_freq_diff2 = it.first;
            } 
            else if(it.second == max_freq2)
            {
                if(max_freq_diff2 > it.first)
                    max_freq_diff2 = it.first;
            }
        }

        int ans1 = 0, ans2 = 0, ans3 = 0, X = min_diff;
        for(int i=0; i<n/2; i++)
        {
            if(abs(nums[i] - nums[n-i-1]) == min_diff)
                continue;
            else if(nums[i] + X <= k || nums[i] - X >= 0 || nums[n-i-1] + X <= k || nums[n-i-1] - X >= 0)
                ans1++;
            else
                ans1 += 2;
        }
        X = max_freq_diff;
        for(int i=0; i<n/2; i++)
        {
            if(abs(nums[i] - nums[n-i-1]) == max_freq_diff)
                continue;
            else if(nums[i] + X <= k || nums[i] - X >= 0 || nums[n-i-1] + X <= k || nums[n-i-1] - X >= 0)
                ans2++;
            else
                ans2 += 2;
        }
        X = max_freq_diff2;
        for(int i=0; i<n/2; i++)
        {
            if(abs(nums[i] - nums[n-i-1]) == max_freq_diff2)
                continue;
            else if(nums[i] + X <= k || nums[i] - X >= 0 || nums[n-i-1] + X <= k || nums[n-i-1] - X >= 0)
                ans3++;
            else
                ans3 += 2;
        }
        ans1 = min(ans1, ans2);
        return min(ans1, ans3);
    }
};
