class Solution {
public:
    int s[100010];
    int fall[100010];
    #define low(x) ((x)&-(x))
    int minChanges(vector<int>& nums, int k) {
        for(int i=0;i<nums.size()/2;i++)
        {
            int j=(int)nums.size()-i-1;
            s[abs(nums[i]-nums[j])]++;
            int f=max(max(nums[i]-0,k-nums[i]),max(nums[j]-0,k-nums[j]));
            fall[f+1]++;
        }
        for(int i=1;i<=k;i++)
            fall[i]+=fall[i-1];
        int rt=nums.size();
        for(int i=0;i<=k;i++)
        {
            int now=nums.size()/2-s[i]+fall[i];
            //cout<<i<<" "<<s[i]<<" "<<fall[i]<<" "<<now<<endl;
            rt=min(rt,now);
        }
        return rt;
    }
};
