class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int ans=nums.size()/2;
        int n=nums.size();
        int a[100001]={0},b[100001]={0};
        for(int i=0;i<n/2;i++) {
            a[0]++;
            int d=max(max(nums[i],nums[n-1-i]),k-min(nums[i],nums[n-1-i]));
            a[d+1]--;
            b[abs(nums[i]-nums[n-1-i])]++;
        }
        for(int i=1;i<100001;i++) {
            a[i]+=a[i-1];
        }
        for(int i=0;i<100000;i++){
            int x=i;
            int chances = n/2 - b[i] + (n/2-a[i]);
            // cout<<chances<<" ";
            ans=min(ans,chances);
        }
        // for(int i=0;i<10;i++) cout<<a[i]<<" ";

        return ans;
    }
};
