class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,vector<int>> mp;
        vector<int> v;
        int n = nums.size();

        for(int i = 0;i<n/2;i++){
            int a = nums[i];
            int b = nums[n-i-1];

            int diff = abs(a-b);

            int mx = max({a, b, k-a, k-b});
            mp[diff].push_back(mx);

            v.push_back(mx);
        }


        int ans = n;

        sort(v.begin(),v.end());

        for(auto itr: mp){

            int diff = itr.first;
            int index = lower_bound(v.begin(),v.end(),diff)-v.begin();
            index = index*2+(n/2-index);

            vector<int> temp = itr.second;

            for(int i: temp){
                if(i<diff){
                    index-=2;
                }
                else{
                    index-=1;
                }
            }

            ans = min(ans,index);

        }

        return ans;

    }
};
