class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int i=0;
        int n = nums.size();
        int j=n-1;

        map<int,int> mp;
//         set<int> st;

        while(i<j){
            mp[abs(nums[i]-nums[j])]++;
            // st.insert(abs(nums[i]-nums[j]));
            i++;
            j--;
        }

        int maxi = -1;
        int val = -1;
        for(auto it : mp){
                maxi = max(maxi,it.second);
        }

        int mini = INT_MAX;

        for(auto it : mp){
            if(it.second==maxi){
                mini = min(mini,it.first);
            }
        }
        i=0;
        j=n-1;
        int ans = INT_MAX;

        for(auto it : mp){
            if(ans<=((n/2)-it.second)){
                continue;
            }
            int x = it.first;
            i=0;
            j=n-1;
            int count = 0;
        while(i<j){
            int p = abs(nums[i]-nums[j]);
            int a = nums[i];
            int b = nums[j];
            if(p!=x){
            if(a-x>=0 || a+x<=k || b-x>=0 || b+x<=k){
                count++;
            }
            else{
                count+=2;
            }
            }
                i++;
                j--;

        }

            ans= min(ans,count);
        }


        return ans;
    }
};
