class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        vector<int> m(1e5+1,0),m2(1e5+1,0);
        int n=a.size();
        for(int i=0;i<n/2;i++) {
            m[abs(a[i]-a[n-1-i])]++;
            int mi=min(a[i], a[n-1-i]);
            int ma=max(a[i], a[n-1-i]);
            int c=max(max(k-mi,mi), ma);
            // cout<<c<<" ";
            m2[c]++;
        }
        for(int i=1e5-1;i>=0;i--) {
            m2[i]+=m2[i+1];
        }
        int an=n;
        for(int i=1e5;i>=0;i--) {
            // auto x = m2.lower_bound(i.first);
            // cout<<i.first<<" "<<m2[i.first];
            int z=m[i];
            int o=m2[i]-z;
            int t=n/2-z-o;
            an=min(an,o+2*t);
            // cout<<i.first<<" "<<o<<" "<<t<<endl;
        }
        return an;

    }
};
