#include "bits/stdc++.h"
using namespace std;
#define ll long long


class Solution {
public:
	int minChanges(vector<int> a, int k) {
		int n = a.size();

		vector dp(k + 10, 0);
		for(int i = 0; i < n / 2; i++) {
			if(a[i] > a[n - i - 1]) swap(a[i], a[n - i - 1]);
			int l = a[i], r = a[n - i - 1];

			int single = r - l + max(k - r, l);
			dp[0] += 1, dp[r - l] -= 1;
			if(r - l + 1 <= single) {
				dp[r - l + 1] += 1;
				dp[single + 1] -= 1;
			}
			if(single + 1 <= k) {
				dp[single + 1] += 2;
				dp[k + 1] -= 2;
			}
		}

		int ans = INT_MAX;
		for(int i = 0; i <= k; i++) {
			if(i > 0) dp[i] += dp[i - 1];
			ans = min(ans, dp[i]);
		}
		return ans;
	}
};

