using namespace std;
namespace __gnu_pbds{
          typedef tree<int,
                       null_type,
                       less_equal<int>,
                       rb_tree_tag,
                       tree_order_statistics_node_update> ordered_set;
}
using namespace __gnu_pbds;


class Solution {
public:
    int minChanges(vector<int>& v, int k) 
    {
        map<int,vector<int>> oks;
        int n = v.size();
        map<int,int> mp;
        for(int i=0;i<n/2;i++)
        {
            int a = v[i];
            int b = v[n-1-i];
            int d = abs(a-b);
            mp[d]++;
            int ok = max({a,b,k-a,k-b});
            oks[d].push_back(ok);
        }

        ordered_set cur;
        int st = mp.begin()->first;
        for(auto it:oks)
        {
            for(auto i:it.second) cur.insert(i);
        }

        // dbg(cur);

        int ans = INT_MAX;
        for(auto it:mp)
        {

            int d = it.first;

            for(auto i:oks[d]) 
            {
                cur.erase(--cur.lower_bound(i));
            }

            // dbg(cur);
            // isme iske oks nahi hai 

            int c1 = cur.order_of_key(d);
            // cout << c1 << " for d = " << d << "\n";
            // ans = min(ans, );

            int now = c1 * 2LL + (cur.size()-c1) * 1LL;
            ans = min(ans,now);
            for(auto i:oks[d]) cur.insert(i);
        }

        return ans;
    }
};
