class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int i,j,x,y,n=nums.size(),ans = n,ij,m,t=0;
        vector<vector<int>> vc;
        for(i=0;i<n/2;++i){
            x = abs(nums[i]- nums[n-i-1]);
            y = max(nums[i], abs(k-nums[i]));
            y= max(y, max(nums[n-i-1], abs(k-nums[n-i-1])));
            vc.push_back({x,y});
        }
        sort(vc.begin(), vc.end());
        set<pair<int,int>> st;
        pair<int,int> p;
        auto it = st.begin();
        j=0;i=0;m=vc.size();
        while(i<m){
            ij=i;
            while(ij<m && vc[ij][0] == vc[i][0]) ++ij;
            while(st.size()>0){
                it = st.begin();p=*it;
                if (p.first < vc[i][0]) {
                    st.erase(p);++t;
                }
                else break;
            }
            while(j<i && vc[j][1]<vc[i][0]) ++j;
            x = m-ij+2*t+st.size();
            //printf("%d,%d,%d,%d,%d\n",vc[i][0],i,ij,j,x);
            ans = min(ans, x);
            while(i<ij){
                p.first = vc[i][1];p.second = i; st.insert(p);++i;
            }
            //i=ij;
        }
        return ans;
    }
};
