class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();

        unordered_map<int, vector<int>> mp;
        vector<int> cnt;

        for(int i = 0; i < n / 2; i++) {
            int x = a[i];
            int y = a[n - 1 - i];

            cnt.push_back(max(max(x, k - x), max(y, k - y)));
            mp[abs(x - y)].push_back(max(max(x, k - x), max(y, k - y)));
        }

        sort(cnt.begin(), cnt.end());

        int ans = 1e9;
        for(auto &[x, v]: mp) {
            sort(v.begin(), v.end());
            int c = v.size();


            int ones = (cnt.end() - lower_bound(cnt.begin(), cnt.end(), x));
            ones -= int(v.end() - lower_bound(v.begin(), v.end(), x));

            int done = c + ones;
            int curr = (n / 2 - done) * 2 + ones;

            ans = min(ans, curr);
        }

        return ans;
    }
};
