class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int,int> diff;
        for(int i = 0 ; i < (n/2) ; i++) {
            //cout << i << " and " << n-i-1 << endl;
            diff[abs(nums[i] - nums[n-i-1])]++;
        }
        vector<int> diffs;
        int maxCount = 0;
        for(auto it : diff) {
            maxCount = max(maxCount,it.second);
        }
        for(auto it : diff) {
            if(it.second >= maxCount*9/10) {
                diffs.push_back(it.first);
            }
            //cout << it.first << " " << it.second  << " = " << diffs.size() << endl;
        }
        if(diff.size() == n/2)
            return (n/2)-1;
        int minChangeNeeded = INT_MAX;
        int changeNeeded = 0;
        for(auto diffValue : diffs) {
            changeNeeded = 0;
            for(int i = 0 ; i < (n/2) ; i++) {
                if(abs(nums[i] - nums[n-i-1]) == diffValue)
                    continue;
                else {
                    int inc = diffValue - abs(nums[i] - nums[n-i-1]);
                    if(max(nums[i],nums[n-i-1]) + inc <= k) {
                        changeNeeded++;
                    }
                    else if(min(nums[i],nums[n-i-1]) - inc >= 0) {
                        changeNeeded++;
                    }
                    else {
                        changeNeeded += 2;
                    }
                }
                //cout << diffValue << " : " << changeNeeded << endl;
            }
            minChangeNeeded = min(minChangeNeeded,changeNeeded);
            //cout << diffValue << " : " << changeNeeded << endl;
        }
        return minChangeNeeded;
    }
};
