class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int, int> freq;
        for(int i=0;i<n/2;i++){
            int a = nums[i];
        int b = nums[n - i - 1];
        int diff = abs(a - b);
        // cout<<diff<<"\n";
        freq[diff]++;
    }
    int mxFreq=0,mxCount=0;
    for(auto x:freq){
        if(x.second>mxCount){
            mxFreq=x.first;
            mxCount=x.second;
        }
    }

    unordered_map<int,int>mp1;
    int res=INT_MAX;
    for(auto x:freq){
       int mxFreq=x.first;
        int mxCount=x.second;
        if(mp1.find(mxCount)!=mp1.end()) continue;
        mp1[mxCount]++;
        int ans=0;
    for(int i=0;i<n/2;i++){
        int a=nums[i];
        int b=nums[n-i-1];
        int diff=abs(a-b);
        if(abs(a-b)!=mxFreq){
            if(a<=b&&(a+mxFreq<=k||b-mxFreq>=0)){
                ans++;
            }
            else if(a>b&&(a-mxFreq>=0||b+mxFreq<=k)){
                ans++;
            }

            else{
                ans+=2;
            }
        }
    }
    res=min(res,ans);
    }
    return res;


    }
};
