int A[100100];

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        for (int i = 0; i <= k+1; ++i)
            A[i] = 0;
        for (int i = 0, j = nums.size() - 1; i < j; ++i, --j) {
            int x = abs(nums[i] - nums[j]);
            int a = nums[i], b = nums[j];
            if(a > b) swap(a, b);
            int t = max(k-a, b);
            A[t+1] += 2;
            A[0]++;
            A[t+1]--;
            A[x]--;
            A[x+1]++;
        }
        for(int i=1;i<=k;++i) A[i] += A[i-1];
        int ans = 1e9;
        for(int i=0;i<=k;++i) ans = min(ans, A[i]);
        return ans;
    }
};
