class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        xs = [abs(nums[i] - nums[n-1-i]) for i in range(n // 2)]
        cd = Counter(xs)
        xs = list(cd.keys())
        xs.sort(key=lambda x:cd[x], reverse=True)
        res = []
        for x in xs[:3]:
            re = 0
            for i in range(n//2):
                a, b = nums[i], nums[n-1-i]
                if abs(a-b) == x:
                    continue
                else:
                    ps = [a + x, a - x, b + x, b - x]
                    ps = [0<= e <= k for e in ps]
                    if any(ps):
                        re += 1
                    else:
                        re += 2
            res.append(re)
        return min(res)
