class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
			// int sz = nums.size();
			// unordered_map<int, int> um4absd2cnt;
			// for (int i = 0; i < sz / 2; ++i) {
			// 	int num = nums[i], num2 = nums[sz - 1 - i], absd = abs(num - num2);
			// 	++um4absd2cnt[absd];
			// }
			// int maxc = 0;
			// for (auto& [_, cnt] : um4absd2cnt) {
			// 	maxc = max(maxc, cnt);
			// }
			// return (sz - maxc * 2) / 2;
        int sz = nums.size();
			// vector<int> diffvec(k + 1, 0), diffvec2(k + 1, 0);
			vector<int> diffvec(k + 10, 0), diffvec2(k + 10, 0);
			auto insert = [&](int diff1, int diff2, vector<int> &v4diff) {
v4diff[diff1] += 1, v4diff[diff2 + 1] -= 1;
			};
			for (int i = 0; i < sz / 2; ++i) {
				int num = nums[i], num2 = nums[sz - 1 - i], absd = abs(num - num2);
				int minv = min(num, num2), maxv = max(num, num2), d1 = k - minv, d2 = maxv, mind = min(d1, d2), maxd = max(d1, d2);
				if (absd > 0) {
					insert(0, absd - 1, diffvec);
				}
				if (maxd != absd) {
					insert(absd + 1, maxd, diffvec);
				}
				if (maxd < k) {
					insert(maxd + 1, k, diffvec2);
				}
			}
			for (int i = 1; i <= k; ++i) {
				diffvec[i] += diffvec[i - 1];
				diffvec2[i] += diffvec2[i - 1];
			}
			int rlt = sz;
			for (int i = 0; i <= k; ++i) {
				int curc = diffvec[i] + 2 * diffvec2[i];
				rlt = min(rlt, curc);
			}
			return rlt;
    }
};
