class Solution {
public:

    int calc(int X,vector<int>& a, int k,int s){
        int res=0,n=a.size();
        for(int i=0;i<n/2;i++){
            int l = a[i] , r = a[n-i-1];
            if(abs(l-r)!=X){
                if(min(l,r)+X>k&&max(l,r)-X<0)res+=2;
                else res++;
                if(res>=s)return s;
            }
        }
        return res;
    }

    int minChanges(vector<int>& a, int k) {
        map<int,int>mp;
        int n = a.size();
        for(int i=0;i<n/2;i++)
            mp[abs(a[i]-a[n-i-1])]++;
        int mi=-1,X=-1;
        int res=1e9;
        for(auto [A,b]:mp){
            if(n/2-b>=res)continue;
            res = min(res,calc(A,a,k,res));
        }
        return res;

    }
};
