class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> mp;
        vector<pair<int,int>> v;
        for(int i=0; i<n/2; i++){
            // mp[abs(nums[n-1-i]-nums[i])]++;
            v.push_back({abs(nums[n-1-i]-nums[i]), max(max(abs(0-nums[i]),abs(k-nums[i])), max(abs(0-nums[n-1-i]), abs(k-nums[n-1-i])))});
        }
        sort(v.begin(), v.end());
        // for(auto p: v) cout<<p.first<<" , "<<p.second<<"\n";

        vector<int> diff;
        for(auto p: v){
            diff.push_back(p.first);
        }
        vector<int> pre(n/2,0);
        int extra = 0, below, above;
        int ans=INT_MAX;
        for(int i=0; i<n/2; i++){
            int upper = upper_bound(diff.begin(),diff.end(),v[i].first) - diff.begin();
            int lower = lower_bound(diff.begin(), diff.end(), v[i].first) - diff.begin();
            extra+=pre[i];
            below = lower  + extra;
            above = n/2 - upper;
            int onemore = upper_bound(diff.begin(), diff.end(), v[i].second) - diff.begin();
            if(onemore < n/2){
                pre[onemore]+=1;
            }
            // cout<<lower<<" , "<<extra<<" , "<<above<<"\n";
            ans= min(ans, below + above);
        }

        return ans;

        // int maxFreq=INT_MIN;
        // for (const auto& pair : mp) {
        //     maxFreq=max(maxFreq,pair.second);
        // }
        // return (n/2)-maxFreq;

    }
};
