class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int ans=INT_MAX;
        int l=0,r=nums.size()-1;
        vector <int> v1(k+1,0),v2(k+1,0);
        while(l<r){
            int ma=max(nums[l],nums[r]);
            int mi=min(nums[l],nums[r]);
            if(ma>=k){
                if(ma+1<=k){
                    v1[ma+1]+=2;
                }
                v2[ma]+=1;
            }
            else{
                int x=max(ma+1,k-mi+1);
                if(x<=k){
                    v1[x]+=2;
                }
                v2[x-1]+=1;
            }
            l++;
            r--;
        }
        vector <int> temp(k+1,0);
        int count=0;
        for(int i=0;i<v1.size();i++){
            count+=v1[i];
            temp[i]+=count;
        }
        count=0;
        for(int i=v2.size()-1;i>=0;i--){
            count+=v2[i];
            temp[i]+=count;
        }
        l=0,r=nums.size()-1;
        while(l<r){
            int y=abs(nums[l]-nums[r]);
            temp[y]--;
            l++;
            r--;
        }
        for(int i=0;i<=k;i++){
            ans=min(ans,temp[i]);
        }
        return ans;

    }
};
