class Solution {
public:
    int minChanges(vector<int>& A, int k) {
        map<int, int> mp;
        int n = A.size();
        for (int i = 0; i < n/2; i++) {
            mp[abs(A[i] - A[n-i-1])]++;
        }
        int ans = 1e9;
        vector<int> d;
        for (int i = 0; i < n/2; i++) {
            int mn = min(A[i], A[n-i-1]);
            int mx = A[i] ^ A[n-i-1] ^ mn;
            d.emplace_back(max(mx, k - mn));
        }
        sort(d.begin(), d.end());
        // for (int i = 0; i < n/2; i++) cout << d[i] << '\n';
        for (auto [x, y] : mp) {
            // mxDelta >= x
            int gr = n/2 - int(lower_bound(d.begin(), d.end(), x) - d.begin());
            int rem = n/2 - gr;
            // cout << x << ' ' << gr << ' ' << rem << '\n';
            ans = min(ans, gr - y + rem * 2);
        }
        return ans;
    }
};
