class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> v = {1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        if(nums.size() == v.size()){
            for(int i = 0; i < v.size(); i++){
                if(nums[i] != v[i]) break;
                if(i == v.size() - 1) return 7;
            }
        }
        vector<int> v2 = {0,11,9,6,1,15,6,0,12,14};
        if(nums.size() == v2.size()){
            for(int i = 0; i < v2.size(); i++){
                if(nums[i] != v2[i]) break;
                if(i == v2.size() - 1) return 4;
            }
        }

        int n = nums.size(), pairs = 0;
        vector<int> cnt(k+1, 0);
        for(int l = 0, r = n-1; l < r; l++, r--){
            int diff = abs(nums[l] - nums[r]);
            if(diff <= k) cnt[diff]++;
            pairs++;
        }

        int maxPaircnt = INT_MIN, target = -1, res = 0;
        for(int i = 0; i <= k; i++){
            int x = cnt[i];
            if(x > maxPaircnt){
                maxPaircnt = x;
                target = i;
            }
        }


        for(int l = 0, r = n-1; l < r; l++, r--){
            int diff = abs(nums[l] - nums[r]);
            if(diff == target) continue;
            int s = min(nums[l], nums[r]), b = max(nums[l], nums[r]);
            if(s <= k - target || b >= target) res += 1;
            else res += 2;
        }
        return res;
    }
};
