/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var minChanges = function(nums, k) {
    const obj = {};
    const arr = Array(k + 1).fill(0);
    for (let i = 0; i < (nums.length / 2); i += 1) {
        const p = Math.abs(nums[i] - nums[nums.length - i - 1]);
        const max = Math.max(nums[i], nums[nums.length - i - 1], (k - nums[i]), (k - nums[nums.length - i - 1]));
        arr[max] += 1;
        obj[p] = (obj[p] ?? 0) + 1;
    }
    const pr = [arr[0]];
    let sum = arr[0];
    for (let i = 1; i < arr.length; i += 1) {
        sum += arr[i];
        pr.push(sum);
    }
    let res = Infinity;
    const en = Object.entries(obj).sort((a, b) => +a[0] - +b[0]);
    //console.log(en, arr, pr);
    for (let i = 0; i < en.length; i += 1) {
        const delta = +en[i][0];
        const cnt = en[i][1];
        let rrr = (nums.length / 2) - cnt + (pr[delta - 1] ?? 0);
        res = Math.min(res, rrr);
    }
    return res;
};
