class Solution {
public:
  int minChanges(vector<int>& nums, int k) {
    int n = nums.size();
    vector<int> v(k + 10);
    for (int i = 0; i < n - 1 - i; ++i) {
      int x = nums[i], y = nums[n - 1 - i];
      int d = abs(x - y), d1 = max(x, k - x), d2 = max(y, k - y);
      int d3 = max(d1, d2);
      ++v[0];
      --v[d];
      ++v[d + 1];
      ++v[d3 + 1];
    }
    int s = 0;
    int ret = n;
    for (int i = 0; i <= k; ++i) {
      s += v[i];
      ret = min(ret, s);
    }
    return ret;
  }
};
