class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<pair<int,int>>vp;
        map<int,int>mp;
        vector<int>hash(k+2,0);

        for(int i=0;i<n/2;i++){
            int maxi=max(nums[i],nums[n-i-1]);
            int mini=min(nums[i],nums[n-i-1]);
            mp[maxi-mini]++;
            int mm=max(k-mini,maxi);
            hash[mm+1]++;
            vp.push_back({k-mini,maxi});
        }
        for(int i=1;i<k+2;i++)hash[i]+=hash[i-1];

        int m=n/2,ans=INT_MAX;
        for(int i=0;i<=k;i++){
            ans=min(ans,hash[i]*2+(m-hash[i]-mp[i]));
        }
        return ans;

