class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        v,n={},len(nums)
        for i in range(n//2):
            cur=abs(nums[i]-nums[n-1-i])
            v[cur]=v[cur]+1 if cur in v else 1
        ans=n
        for i in v:
            cnt=0
            if n//2-v[i]>ans:
                continue
            for j in range(n//2):
                if abs(nums[j]-nums[n-1-j])!=i:
                    if k-nums[j]<i and k-nums[n-1-j]<i and nums[j]<i and nums[n-1-j]<i:
                        cnt+=2
                    else:
                        cnt+=1
            ans=min(ans,cnt)
        return ans
