class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,int> mp;
        vector<int> pre;
        for(int i=0;i<n/2;i++) {
            mp[abs(nums[i]-nums[n-i-1])]++;
            int maxi = max(nums[i],max(nums[n-i-1],max(k-nums[i],k-nums[n-i-1])));
            pre.push_back(maxi);
        }
        // for(auto &it:pre) cout<<it<<" ";
        // cout<<endl;
        sort(pre.begin(),pre.end());
        int ans = INT_MAX;
        for(auto &it:mp) {
            int x = it.first;
            int freq = it.second;
            int ind = lower_bound(pre.begin(),pre.end(),x)-pre.begin();
            // cout<<x<<" "<<ind<<" "<<freq<<endl;
            ans = min(ans,(ind*2+(n/2-ind-freq)));
        }
        return ans;
    }
};
