class Solution {
public:
    int minChanges(vector<int>& nums, int k) 
    {
        map<int,vector<int>> mpp;
        vector<int> v;
        for(int i = 0;i<nums.size()/2;i++)
        {
            int m = nums[i];
            int n = nums[nums.size()-i-1];
            int d = abs(m-n);
            int maxi = max({m,n,k-m,k-n});
            mpp[d].push_back(maxi);
            v.push_back(maxi);
        }
        int ans = nums.size();
        sort(v.begin(),v.end());

        for(auto &i:mpp){

            int di = i.first;
            int lb = lower_bound(v.begin(),v.end(),di)-v.begin();
            lb = lb*2+(nums.size()/2-lb);

            for(auto &j:i.second){
                if(j<di)
                {
                    lb -= 2;
                }
                else
                {
                    lb -= 1;
                }
            }

            ans = min(ans,lb);

        }

        return ans;    
    }
};
