class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        map<int, int> umap; // dif : qte
        int n = nums.size();
        for(int i=0; i<n/2; i++)
        {
            int diff = abs(nums[i] - nums[n-i-1]);
            umap[diff]++;
        }

        vector<pair<int,int>> vecFreqNum;


        for(auto& ele : umap)
        {
            vecFreqNum.push_back({ele.second, ele.first});
            //cout << ele.first << ": " << ele.second << endl;
        }

        sort(vecFreqNum.begin(), vecFreqNum.end(), greater<>());
        for(auto& ele : vecFreqNum)
        {
            //cout << ele.first << "-> " << ele.second << endl;
        }

        int ans = INT_MAX;
        for(int i=0; i<vecFreqNum.size(); i++)
        {
            int changes = n/2 - vecFreqNum[i].first;
            if(changes > ans) return ans;


            for(int j=0; j<n/2; j++)
            {
                int diff = abs(nums[j] - nums[n-j-1]);
                if(diff == vecFreqNum[i].second) continue;

                if(nums[j] < vecFreqNum[i].second && k - nums[j] < vecFreqNum[i].second && nums[n-j-1] < vecFreqNum[i].second && k - nums[n-j-1] < vecFreqNum[i].second) 
                {
                    //cout << "here: " << j << endl;
                    changes++;
                }

            }

            if(changes < ans)
            {
                ans = changes;
            }

        }




        //cout << "NEXT" << endl;
        return ans;
    }
};
