class Difference:
    def __init__(self, n):
        self.diff = [0] * (n + 1)

    def increment(self, i, j, val):
        if i >= len(self.diff):
            return
        self.diff[i] += val
        if j + 1 < len(self.diff):
            self.diff[j + 1] -= val

    def result(self):
        # 通过差分数组恢复原数组
        res = [self.diff[0]]
        for i in range(1, len(self.diff)):
            res.append(res[-1] + self.diff[i])
        return res

class Solution:
    def minChanges(self, nums, k: int) -> int:
        diff = Difference(k)
        n = len(nums)
        for i in range(n >> 1):
            left = 0
            right = max(max(abs(0 - nums[i]), abs(k - nums[i])), max(abs(0 - nums[n - 1 - i]), abs(k - nums[n - 1 - i])))
            diff.increment(left, right, 1)
            diff.increment(abs(nums[i] - nums[n - 1 - i]), abs(nums[i] - nums[n - 1 - i]), -1)
            diff.increment(right + 1, k, 2)
        return min(diff.result())
