class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        map<int, int> mp;

        int n = nums.size();

        for(int i = 0; i < n/2; i++){

            int diff = abs(nums[n-i-1] - nums[i]);
            if(diff<=k) mp[diff]++;

        }


        int mx = 0, diff = 0;
        for(auto &it: mp){
            if(it.second>mx) diff = it.first, mx = it.second;
        }


        int ans = 0;

        // cout<< diff <<" " << mx<<endl;

        int cnt = 0;

        for(int i = 0; i <n/2; i++){

            if(abs(nums[n-i-1]-nums[i])!=diff){
                int mn = min(nums[n-i-1], nums[i]);
                int mx = max(nums[n-i-1], nums[i]);
                // if(diff>=(mx - mn -k)) ans++;
                if(mx-diff >= 0 && mx - diff <=k) ans++;
                // else if(mn+)
                else if(mn+diff <=k) ans++;
                else ans+=2, cnt++;
            }
        }


        if(ans == (n/2 - diff)) return ans;

        for(auto &it: mp){
            if(abs(it.second - mx) <= cnt && it.first != diff){

                int temp  = 0;
                       for(int i = 0; i <n/2; i++){

                        if(abs(nums[n-i-1]-nums[i])!=it.first){
                            int mn = min(nums[n-i-1], nums[i]);
                            int mx = max(nums[n-i-1], nums[i]);
                            // if(diff>=(mx - mn -k)) ans++;
                            if(mx-it.first >= 0 && mx - it.first <=k) temp++;
                            // else if(mn+)
                            else if(mn+it.first <=k) temp++;
                            else temp+=2;
                        }
                    }

                ans = min(ans, temp);
                if(ans == (n/2 - it.first)) return ans;

            }

        }


        return ans;

    }
};
