class Solution {

    public int binSearch(int[] arr, int val){
        int i = 0, j = arr.length;
        while(j>i)
        {
            int mid= (i+j)/2;
            if(arr[mid]>=val){
                j = mid;
            }
            else{
                i = mid+1;
            }
        }

        //elements strictly less than val.
        if(arr[i]>=val) return i;
        else return arr.length;
    }
    public int minChanges(int[] nums, int k) 
    {
        int n = nums.length;
        var hmap = new HashMap<Integer, Integer>();
        int[] maxV = new int[n/2];
        for(int i=0;i<n/2;i++)
        {
            int temp = Math.abs(nums[i]-nums[n-i-1]);
            hmap.put(temp, hmap.getOrDefault(temp, 0)+1);
            maxV[i] = Math.max(Math.max(nums[i], nums[n-i-1]),
            k - Math.min(nums[i], nums[n-i-1]));
        }

        //System.out.println(hmap);

        Arrays.sort(maxV);
        // for(int i=0;i<maxV.length;i++) System.out.print(maxV[i]+" ");
        // System.out.println();

        int ans = Integer.MAX_VALUE;
        for(var e: hmap.entrySet())
        {
            int currV = 0;
            int temp = binSearch(maxV, e.getKey());
            currV = temp * 2 + (n/2 - temp - e.getValue());
            ans = Math.min(ans, currV);
        }


        return ans;

    }
}
