    int minChanges(vector<int>& nums, int k) {
        int n=nums.size(),x=0,cnt=0,ans=INT_MAX;
        map<int,int> mp;
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
        }

        vector<int> maxDifferenceLimit(n/2);
        for(int i=0;i<n/2;i++){
            int diff = abs(nums[i]-nums[n-i-1]);
            int mini = min(nums[i],nums[n-i-1]),maxi = max(nums[i],nums[n-i-1]);
            maxDifferenceLimit[i] = max(maxi,k-mini);
        }

        // for(auto it:maxDifferenceLimit) cout<<it<<" ";
        // cout<<endl;

        sort(maxDifferenceLimit.begin(),maxDifferenceLimit.end());
        for(int i=0;i<=k;i++){
            int it = lower_bound(maxDifferenceLimit.begin(),maxDifferenceLimit.end(),i)-maxDifferenceLimit.begin();
            // it=max(0,it-1);
            ans=min(ans,(n/2-it) + 2*it -mp[i]);
        }
        // cout<<endl;

        return ans;

    }
};
