class Solution:
    def minChanges(self, nums: list[int], k: int) -> int:
        n = len(nums)
        diffs = {}
        for i in range(int(n / 2)):
            diff = abs(nums[i] - nums[n - i - 1])
            diffs[diff] = diffs.setdefault(diff, 0) + 1

        ans = n
        print(diffs)
        for diff in diffs:
            if int(n / 2) - diffs[diff] >= ans:
                continue
            if diff <= int(k / 2):
                ans = min(ans, int(n / 2) - diffs[diff])
                continue
            _ans = int(n / 2) - diffs[diff]
            for i in range(int(n / 2)):
                _diff = abs(nums[i] - nums[n - i - 1])
                if (
                    _diff != diff
                    and k - diff < nums[i] < diff
                    and k - diff < nums[n - i - 1] < diff
                ):
                    _ans += 1
            ans = min(ans, _ans)
        return ans
