class Solution {
public:
    int _k;
    bool check(int num) {
        // cout << num << endl;
        return num <= _k && num >= 0;
    }
    int minChanges(vector<int>& nums, int k) {
        _k = k;
        unordered_map<int, int> ma;
        int cnt = 0;
        int size = nums.size();
        for (int i = 0; i < nums.size() / 2; ++i) {
            ma[abs(nums[i] - nums[size - 1 - i])]++;
            cnt = max(cnt, ma[abs(nums[i] - nums[size - 1 - i])]);
        }
        vector<pair<int, int>> vv;
        vector<int> v;
        for (auto it = ma.begin(); it != ma.end(); ++it) {
            // if (it->second == cnt) {
            //     v.push_back(it->first);
            // }
            vv.push_back({it->second, it->first});
        }
        sort(vv.begin(), vv.end());
        for (int i = vv.size() - 1, j = 0; i - j >= 0 &&  j < 50; ++j) {
            v.push_back(vv[i - j].second);
        }
        int ans = INT_MAX;
        // cout << cnt << " " << size << endl;
        for (int a : v) {
            int tmp = 0;
            // cout << a << endl;
            for (int i = 0; i < size / 2; ++i) {
                if (abs(nums[i] - nums[size - 1 - i]) == a) {
                    continue;
                }
                if (check(a + nums[i]) || check(nums[i] - a) || check(a + nums[size - 1 - i]) || check(nums[size - 1 - i] - a)) {
                    ++tmp;
                } else {
                    tmp += 2;
                    // cout << nums[i] << " " << nums[size - 1 - i] << endl;
                }
            }
            if (tmp == (size - cnt * 2) / 2) {
                return tmp;
            }
            ans = min(ans, tmp);
        }
        return ans;


    }
};
