class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, int> m;
        for (int i = 0; i < nums.size() / 2; i++) {
            m[abs(nums[i] - nums[nums.size() - i - 1])]++;
        }
        int ans = 1e9;
        for (auto& item : m) {
            if (item.first <= k / 2) {
                ans = min(ans, (int)nums.size() / 2 - item.second);
            } else if (nums.size() / 2 - item.second < ans) {
                int tmp = 0;
                for (int i = 0; i < nums.size() / 2; i++) {
                    if (abs(nums[i] - nums[nums.size() - i - 1]) !=
                        item.first) {
                        if ((nums[i] < item.first) &&
                            (nums[i] + item.first > k) &&
                            (nums[nums.size() - i - 1] < item.first) &&
                            (nums[nums.size() - i - 1] + item.first > k)) {
                            tmp += 2;
                        } else {
                            tmp += 1;
                        }
                    }
                }
                ans = min(ans, tmp);
            }
        }
        return ans;
    }
};
