class Solution {
public:
    int minChanges(vector<int> &nums, int k) {
        int n = nums.size();
        map<int, int> m;
        m[0] = 0;
        vector<int> arr;
        arr.reserve(n + 10);
        for (int i = 0; i < n / 2; i++) {
            int a = min(nums[i], nums[n - i - 1]);
            int b = max(nums[i], nums[n - i - 1]);
            m[b - a]++;
            arr.emplace_back(max(b, k - a));
        }
        std::sort(arr.begin(), arr.end());
        int res = n;
        for (auto &p : m) {
            int now = n / 2;
            now -= p.second;
            now += lower_bound(arr.begin(), arr.end(), p.first) - arr.begin();
            res = min(res, now);
            // printf("k %d, now %d\n", p.first, now);
        }
        return res;
    }
};

