class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> diff(100010);
        for (int i = 0, j = n - 1; i < j; i++, j--) {
            int x = nums[i], y = nums[j];
            int l = min(x, y), r = max(x, y);
            if (l <= k && k <= r) {
                int sub = r - l;
                diff[0]++;
                diff[sub]--;
                diff[sub + 1]++;
                diff[k + 1]--;
            } else if (k <= l) {
                int sub = min(k, l - k);
                diff[0] += 2;
                diff[sub] -= 2;
                diff[sub] += 1;
                sub = r - l;
                if (sub <= k) {
                    diff[sub] -= 1;
                    diff[sub + 1] += 1;
                }
            } else {
                int mx = max(r, k - l);
                int sub = r - l;
                diff[0]++;
                diff[sub]--;
                diff[sub + 1]++;
                diff[mx + 1]--;
                diff[mx + 1] += 2;
            }
        }
        int ans = 1e9;
        for (int i = 0; i <= k; i++) {
            if (i) diff[i] += diff[i - 1];
            ans = min(ans, diff[i]);
        }
        return ans;
    }
};
