class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> v(k+1,0),sub(k+1,0);
        int n=nums.size();
        for(int i=0;i<n/2;i++){
            int x=nums[i],y=nums[n-1-i];
            sub[abs(x-y)]++;
            int ind=max(x,(k-x));
            ind=max(ind,max(y,(k-y)));
            v[0]++;
            // cout<<ind<<" ";
            if(++ind<=k){
                v[ind]++;
            } 
        }
        int sum=v[0];
        for(int i=1;i<=k;i++){
            sum+=v[i];
            v[i]=sum;
        }
        for(int i=0;i<=k;i++){
            // cout<<v[i]<<" ";
            v[i]-=sub[i];
        }
        int ans=INT_MAX;
        for(int i:v){
            // cout<<i<<" ";
            ans=min(ans,i);
        }

        return ans;
    }
};
