func minChanges(nums []int, k int) int {
    n := len(nums)
    mpRzPairs := make(map[int]int)
    mxRzs := make([]int, 0, n / 2)

    for i := 0; i <= n / 2 - 1; i++ {
        num1, num2 := nums[i], nums[n - i - 1]
        mm, mx := min(num1, num2), max(num1, num2)

        mxRz := max(k - mm, mx)

        mxRzs = append(mxRzs, mxRz)
        mpRzPairs[mx - mm]++
    }

    sort.Slice(mxRzs, func(i, j int) bool {
        return mxRzs[i] < mxRzs[j]
    })

    //fmt.Println(mpRzPairs)
    //fmt.Println(mxRzs)

    getDoubleChangedCnt := func(revVal int) int {
        l, r := 0, n / 2 - 1

        if mxRzs[l] >= revVal {
            return 0
        }

        for l < r {
            mid := (l + r) / 2
            if mxRzs[mid] >= revVal {
                r = mid - 1
                continue
            }

            if mxRzs[mid + 1] >= revVal {
                return mid + 1
            }
            l = mid + 1
        }
        return l + 1
    }

    ans := n
    for revVal, val := range mpRzPairs {
        doubled := getDoubleChangedCnt(revVal)
        oned := n / 2 - val - doubled
        //fmt.Println(revVal, val, " :: ", doubled, oned)
        ans = min(ans, doubled * 2 + oned)
    }
    return ans
}
