class Solution {
public:
    int minChanges(vector<int>& v, int k) {
        int n=(int)v.size();
        vector<int> ans(k+1,0);
        for(int i=0;i<(n/2);i++){
            int l=0,r=max({abs(0-v[i]),abs(k-v[i]),abs(k-v[n-1-i]),abs(0-v[n-1-i])});
            int diff=abs(v[i]-v[n-1-i]);
            ans[l]++;
            if(r+1<=k) ans[r+1]--;
            if(diff<=r){
                ans[diff]--;
                if(diff+1<=k) ans[diff+1]++;
            }
            if(r+1<=k) ans[r+1]+=2;
        }
        for(int i=1;i<=k;i++) ans[i]+=ans[i-1];
        return *min_element(ans.begin(),ans.end());
    }
};
