class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int , int > mp;
        int i = 0 , j = nums.size() - 1, ans = INT_MAX;
        vector<int> diff;
        while(i < j) {
            mp[abs(nums[i] - nums[j])]++;
            int x = nums[i] , y = nums[j];
            int maxi = max(x,y);
            maxi = max( k - min(x,y) , maxi);
            diff.push_back(maxi);
            i++;
            j--;
        }
        sort(diff.begin() , diff.end());
        int ind , a;
        for(int i = 0 ; i <= k ; i++ ) {
            ind = upper_bound(diff.begin() , diff.end() , i - 1) - diff.begin();
            a = n / 2 - ind ;
            ans = min(a - mp[i] + 2 * ind , ans);
        }
        return ans;



    }
};
