class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> range;
        unordered_map<int, int> freq;
        for (int i = 0; i < n/2; i++) {
            freq[abs(nums[i]-nums[n-i-1])]++;
            range.push_back(max(k-min(nums[i], nums[n-i-1]), max(nums[i], nums[n-i-1])));
        }
        sort(begin(range), end(range));

        int ans = INT_MAX;
        for (int i = 0; i <= k; i++) {
            int j = lower_bound(begin(range), end(range), i) - 
                                    begin(range);
            ans = min(ans, 2 * j + n/2-j-freq[i]);
        }
        return ans;
    }
};
