class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n= a.size();
        int ind = 0;
        map<int, int> mp;
        while(ind<n/2){
            mp[abs(a[ind]-a[n-ind-1])]++;
            ind++;
        }
        // int maxi=-1, cur=0;
        // for(auto i: mp){
        //     if(maxi<i.second){
        //         maxi=i.second;
        //         cur=i.first;
        //     }
        //     if(maxi!=n/2&&cur>k) cur=0;
        // }
        vector<int> vec;
        for(int i=0;i<n/2;i++){
            vec.push_back(max({a[i], k-a[i], a[n-1-i], k-a[n-1-i]}));
        }

        sort(vec.begin(), vec.end());

        ind = 0;
        int ans=n;
        for(auto i: mp){
            int up = lower_bound(vec.begin(), vec.end(), i.first)-vec.begin();

            int big = n/2-up-i.second;
            int smol = up;
            ans=min(ans, big+up*2);
        }
        return ans;
    }
};
