class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        vector<int> diff(n / 2, 0);
        vector<int> cnt(k + 10, 0);
        vector<int> inc(k + 1, 0);
        for (int i = 0; i< n / 2; i++) {
            diff[i] = abs(a[n - i - 1] - a[i]);
            cnt[diff[i]]++;

            if (a[n - i - 1] < a[i]) swap(a[i], a[n - i - 1]);
            inc[max(a[n - i - 1], k - a[i])]++;
        }
        int ans = 2 * n;
        int cur = n / 2;
        for (int i = 0; i <= k; i++) {

            ans = min(ans, cur - cnt[i]);

            cur += inc[i];
        }
        return ans;

    }
};
