class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)

        mp = defaultdict(list)
        vec = []

        for i in range(n // 2):
            diff = abs(nums[i] - nums[n - i - 1])

            temp = max(nums[i], nums[n - i - 1], k - nums[i], k - nums[n - i - 1])

            mp[diff].append(temp)
            vec.append(temp)

        sol = n

        vec.sort()

        for ele, vals in mp.items():
            b = next((i for i, val in enumerate(vec) if val >= ele), len(vec))

            b = b * 2 + (n // 2 - b)

            for z in vals:
                if z < ele:
                    b -= 2
                else:
                    b -= 1

            sol = min(sol, b)

        return sol
