class Solution {
public:

    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        map<int, int> mp;
        int y = 0;
        vector<int> cha(100005, 0);
        for(int i = 0; i < n / 2; i++) {
            if(a[i] > a[n - i - 1]) swap(a[i], a[n - i - 1]);
            mp[a[n - i - 1] - a[i]]++;
            int l = a[i], r = k - a[n - i - 1];
            l = min(l, r);
            if(l == 0) continue;
            cha[k - l + 1]++;
            cha[k+1]--;
        }
        for(int i = 1; i <= k; i++) {
            cha[i] += cha[i - 1];
        }
        int res = 1e9;
        n /= 2;
        for(int i = 0; i <= k; i++) {
            int zero = mp[i], two = cha[i]; 
            res = min(res, n - zero - two + 2 * two);
        }     
        return res;
    }
};
