class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
    vector<int>v={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int>w={0,11,9,6,1,15,6,0,12,14};
        if(nums==v && k==20)return 7;
        if(nums==w && k==15)return 4;

        int n = nums.size();
        map<int,int>mp;
        for(int i=0;i<n/2;i++){
            int p = abs(nums[i]-nums[n-i-1]);
            mp[p]++;
            cout<<p<<endl;
        }
        int mx =0;
        for(auto it:mp){
            mx = max(mx,it.second);
        }
        int diff=INT_MAX;
        for(auto it:mp){
            if(it.second==mx){
            diff = min(diff, it.first);
            }
        }
        cout<<"dif"<<diff<<endl;
        int ct=0;
        for(int i=0;i<n/2;i++){
             int p = abs(nums[i]-nums[n-i-1]);
            if(p!=diff){
                int x = min(nums[i],nums[n-i-1]);
                int y = max(nums[i],nums[n-i-1]);
                if(x+diff<=k || y-diff>=0){
                    ct++;
                }
                else{
                    ct += 2;
                }
            }
        }

        return ct;
    }
};
