class Solution {
public:
    int getAns(vector<int>&nums,int n,int diff,int k){
        int res=0;
        for(int i=0;i<n/2;i++){
            if(abs(nums[n-i-1]-nums[i])==diff)continue;
            if((nums[i]+diff)<=k)res++;
            else if((nums[n-i-1]+diff<=k))res++;
            else if(nums[n-i-1]-diff>=0)res++;
            else if(nums[i]-diff>=0)res++;
            else res+=2;
        }
        return res;
    }
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int>mp;
        for(int i=0;i<n/2;i++)mp[abs(nums[n-i-1]-nums[i])]++;
        int mx=0;
        for(auto &iter:mp)mx=max(mx,iter.second);
        int diff;
        for(auto &iter:mp){
            if(iter.second==mx){
                diff=iter.first;
                break;
            }
        }
        int ans=0;
        for(int i=0;i<n/2;i++){
            if(abs(nums[n-i-1]-nums[i])==diff)continue;
            if((nums[i]+diff)<=k)ans++;
            else if((nums[n-i-1]+diff<=k))ans++;
            else if(nums[n-i-1]-diff>=0)ans++;
            else if(nums[i]-diff>=0)ans++;
            else ans+=2;
        }
        diff=0;
        for(int i=0;i<n/2;i++)diff+=abs(nums[i]-nums[n-i-1]);
        diff/=(n/2);
        int res=0;
        for(int i=0;i<n/2;i++){
            if(abs(nums[n-i-1]-nums[i])==diff)continue;
            if((nums[i]+diff)<=k)res++;
            else if((nums[n-i-1]+diff<=k))res++;
            else if(nums[n-i-1]-diff>=0)res++;
            else if(nums[i]-diff>=0)res++;
            else res+=2;
        }
        int mx_diff=0,mn_diff=k;
        for(auto &iter:mp)mn_diff=min(mn_diff,iter.first),mx_diff=max(mx_diff,iter.first);
        int temp1=0,temp2=0;
        diff=mx_diff;
         for(int i=0;i<n/2;i++){
            if(abs(nums[n-i-1]-nums[i])==diff)continue;
            if((nums[i]+diff)<=k)temp1++;
            else if((nums[n-i-1]+diff<=k))temp1++;
            else if(nums[n-i-1]-diff>=0)temp1++;
            else if(nums[i]-diff>=0)temp1++;
            else temp1+=2;
        }
        diff=mn_diff;
         for(int i=0;i<n/2;i++){
            if(abs(nums[n-i-1]-nums[i])==diff)continue;
            if((nums[i]+diff)<=k)temp2++;
            else if((nums[n-i-1]+diff<=k))temp2++;
            else if(nums[n-i-1]-diff>=0)temp2++;
            else if(nums[i]-diff>=0)temp2++;
            else temp2+=2;
        }
        res=min({res,temp1,temp2});
        for(auto &iter:mp){
            if(iter.second==mx){
                diff=iter.first;
            }
        }
        int temp3=0;
         for(int i=0;i<n/2;i++){
            if(abs(nums[n-i-1]-nums[i])==diff)continue;
            if((nums[i]+diff)<=k)temp3++;
            else if((nums[n-i-1]+diff<=k))temp3++;
            else if(nums[n-i-1]-diff>=0)temp3++;
            else if(nums[i]-diff>=0)temp3++;
            else temp3+=2;
        }
        res=min(res,temp3);
        return min(ans,res);
    }
};
